$(document).ready(function(){$(".work__wrap").hover(function(){$(this).find(".icon-link").css("display","block")},function(){$(".icon-link").css("display","none")}),$(window).scroll(function(){$("body").scrollTop()>400?$(".to-top").css("display","block"):$(".to-top").css("display","none")});for(var o=$(".menu__item"),n=0;n<=o.length;n++){var i=o.width()-.1*o.width()*n;o[n].css("width",i+"px")}$('a[href^="#"]').click(function(){elementClick=$(this).attr("href"),destination=$(elementClick).offset().top,$("body").animate({scrollTop:destination},1e3)})});